from hackerrank import various

COLL_1 = [
    "ID         MARKS      NAME       CLASS",
    "1          97         Raymond    7",
    "2          50         Steven     4",
    "3          91         Adrian     9",
    "4          72         Stewart    5",
    "5          80         Peter      6"
]
COLL_2 = [
    "MARKS      CLASS      NAME       ID",
    "92         2          Calum      1",
    "82         5          Scott      2",
    "94         2          Jason      3",
    "55         8          Glenn      4",
    "82         2          Fergus     5"
]


def test_python_time_delta():
    assert "25200" == various.python_time_delta("Sun 10 May 2015 13:54:36 -0700", "Sun 10 May 2015 13:54:36 -0000")
    assert "88200" == various.python_time_delta("Sat 02 May 2015 19:54:36 +0530", "Fri 01 May 2015 13:54:36 -0000")


def test_piling_up():
    assert "Yes" == various.piling_up([4, 3, 2, 1, 3, 4])
    assert "No" == various.piling_up([1, 3, 2])
    assert "No" == various.piling_up([1, 2, 3, 8, 7])
    assert "Yes", various.piling_up([1, 2, 3, 7, 8])


def test_minion_game():
    assert "Stuart 12" == various.minion_game("BANANA")
    assert "Stuart 7501500" == various.minion_game(
        "")


def test_py_collections_namedtuple():
    assert "78.00" == various.py_collections_namedtuple(COLL_1)
    assert "81.00" == various.py_collections_namedtuple(COLL_2)
